INSERT INTO `books` (`id`, `book_name`, `excerption`, `author_id`, `photo_path`, `file_path`, `total_pages`, `created_at`, `updated_at`) VALUES
(2, 'Test Book', '<p>ksdjhksfsdfsdf</p>', 1, '/img/book-covers/default.jpg', NULL, 920, NULL, '2017-11-29'),
(13, 'Зоро', '<p style=\"text-align: justify;\">ИК &bdquo;Офир&ldquo; предлага за първи път на българския читател класическия приключенски роман на Джонстън Маккъли &bdquo;Зоро&ldquo;. Дописвана от различни автори и преиздавана многократно, книгата е позната у нас предимно от многобройните филмови екранизации &mdash; американската &bdquo;Знакът на Зоро&ldquo;, с участието на Дъглас Феърбанкс, мексиканската &bdquo;Голямото приключение на Зоро&ldquo;, италианско-френската с Ален Делон, игралната и анимационни телевизионни версии и др.</p>\r\n<p style=\"text-align: center;\"><span style=\"text-decoration: line-through;\">&nbsp;</span></p>\r\n<blockquote>\r\n<h5 style=\"text-align: center;\"><span style=\"text-decoration: underline;\"><em>Според видния кинокритик Й. Теплиц със Зоро &bdquo;се ражда героят на новото време героят Made in USA, за когото морето е до колене. Новият популярен герой изразява формиращата се философия на американската буржоазия &mdash; &laquo;психологически панамериканизъм&raquo;, който провъзгласява идеите за превъзходството на новата американска нация. Такъв род идеи са помагали да се преодолее комплексът за непълноценност, който младата Америка изпитва към старата европейска цивилизация.&ldquo;</em></span></h5>\r\n</blockquote>', 6, '/img/book-covers/4552.max.jpg', '/pdf/Dzhonstyn_Makkyli_-_Zoro_-4552-b.txt', 251, '2017-11-24', '2017-11-29'),
(26, 'Test Random Name', '<p>adasd</p>', 6, '/img/book-covers/img_5a1ea00d42697', '/pdf/freesofiatour_flyer-105x148mm-front-print.pdf', 222, '2017-11-29', '2017-11-29'),
(32, 'Бенито Хуарес', '<p>Екранизация на романа е филма на режисьора Ханс Кньоцш: &bdquo;Ловци в прериите на Мексико: Бенито Хуарес&ldquo; (Pr&auml;riej&auml;ger in Mexiko: Benito Juarez, 1988). С Гойко Митич в ролята на Мечешко око, Джоко Росич &mdash; трапера &bdquo;Лешоядовия клюн&ldquo;, Кольо Дончев в ролята на Черния Жерар и др.</p>', 12, '/img/book-covers/img_5a1eb1e35c3c7', '/pdf/Karl_Maj_-_Benito_Huares_-1745-b.txt', 350, '2017-11-29', '2017-11-29'),
(33, 'Отшелника', '<p>Един странен мъж се заселва в самотна порутена кула. Кой е той, откъде идва, какви тайни крие хилядолетното отбранително съоръжение, никой не знае. Стари истории се вплитат в настоящето. Алчни и престъпни хора не се спират пред нищо в стремежа си да докопат изключително съблазнителен майорат.</p>', 12, '/img/book-covers/img_5a1eb2cf868f0', '/pdf/Karl_Maj_-_Otshelnika_-3071-b.txt', 229, '2017-11-29', '2017-11-29'),
(34, 'Безсъние', '<p id=\"p-1\" style=\"text-align: center;\">&bdquo;<strong>Безсъние&ldquo; е роман на ужасите от Стивън Кинг, издаден през 1994 година. Както и в &bdquo;То&ldquo; и в &bdquo;Капан за сънища&ldquo;, действието му се развива в измисления град Дери, Мейн.</strong></p>\r\n<p style=\"text-align: center;\"><strong>&nbsp;</strong>Тази книга обяснява донякъде мистичната природа на Дери (виж например &bdquo;То&ldquo;), разкрива и някои от метафизичните концепции около &bdquo;Тъмната кула&ldquo; и миналото на Патрик Денвил.</p>\r\n<p>&nbsp;</p>\r\n<p id=\"p-5\">Ралф Робъртс, пенсиониран вдовец, започва да страда от безсъние. С влошаването на състоянието си той започва да вижда неща, които другите не виждат: аурата на хората или малки човечета в бели дрехи, които нарича &bdquo;малките бели доктори&ldquo; заради външността им. Робъртс започва да възприема иначе недоловими аспекти на действителността и влиянието им върху &bdquo;реалния&ldquo; свят. Открива, че негова позната, на име Луиз Чейс, страда от същото. Тя се присъединява към усилията му да предотврати надигащото се зло и научава истината за &bdquo;докторите&ldquo;. (Луиз и Ралф ги наричат &bdquo;Клото, Лахезис и Атропос&ldquo;, по имената на орисниците от гръцката митология.) Фонът, на който се развива действието, е наближаващият сблъсък между група от защитници на правата на жените и група противници на абортите.</p>', 13, '/img/book-covers/img_5a1eb45649fba', '/pdf/Stivyn_King_-_Bezsynie_-2031-b.txt', 312, '2017-11-29', '2017-11-30');

